import React from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';

interface DashboardProps {
  logout: any;
  // register: any;
  // handleSubmit: any;
  // errors: any;
}

type Inputs = {
  name: string;
  email: string;
  password: string;
};

const Dashboard: React.FunctionComponent<DashboardProps> = ({ logout }) => {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors }
  } = useForm<Inputs>();

  const onSubmit: React.FunctionComponent = async (e): any => {
    e.preventDefault();
    try {
      let res = await fetch('https://httpbin.org/post', {
        method: 'POST',
        body: JSON.stringify({
          name: name,
          email: email,
          mobileNumber: mobileNumber
        })
      });
      let resJson = await res.json();
      if (res.status === 200) {
        setName('');
        setEmail('');
        setMessage('User created successfully');
      } else {
        setMessage('Some error occured');
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div>
      Dashboard
      <div>
        <form onSubmit={handleSubmit(onSubmit)}>
          <input {...register('name', { required: true })} />
          {errors.name && 'Name is Required'}
          <input {...register('email', { required: true, pattern: /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/ })} />
          {errors.email && 'Email is Required'}
          {/* Password must be at least 4 to 8 characters and include one upper case, one lower case letter, and one numeric digit. */}
          <input {...register('password', { required: true, pattern: /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{4,8}$/ })} />
          {errors.password && 'Password is Required'}
          <button type="submit"> Create </button>
        </form>
      </div>
    </div>
  );
};

export default Dashboard;
