import { FormControl, FormHelperText, Input, InputLabel, Button, Menu, MenuItem, Divider } from '@mui/material';
import Card from '@mui/material/Card';
import React, { useState } from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import { EventEmitter } from 'stream';

type Inputs = {
  email: string;
  firstName: string;
  lastName: string;
  gender: string;
  status: string;
};

const genderOptions = ['Man', 'Woman', 'N/A'];

const CreateUserCard: any = () => {
  const [anchorEl, setAnchorEl] = useState(null);
  const open = Boolean(anchorEl);
  const [gender, setGender] = useState('');

  const {
    register,
    handleSubmit,
    watch,
    formState: { errors }
  } = useForm<Inputs>();

  const onSubmit = (e: any) => {
    console.log(e);
    fetch('https://gorest.co.in/public/v2/users', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: 'Bearer 35fbde49d5eb4a5a443446e7ba864d71ed3969a1ee48247811f6733ebe664c93'
      },
      body: JSON.stringify({
        name: `${e.firstName} ${e.lastName}`,
        email: e.email,
        gender: gender,
        status: e.status
      })
    })
      .then((res) => res.json())
      .then((result) => console.log(result))
      .catch((err) => console.log('error'));
  };

  const handleGender = (gender: any) => {
    console.log('handleCloseEvent', gender);
    setAnchorEl(null);
    setGender(gender);
  };

  const handleClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(null);
  };

  return (
    <div>
      <Card sx={{ maxWidth: 345 }}>
        <form onSubmit={handleSubmit(onSubmit)}>
          <FormControl>
            <InputLabel htmlFor="email">Email address</InputLabel>
            <Input id="email" placeholder="Enter Email" aria-describedby="my-helper-text" {...register('email', { required: true, pattern: /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/ })} />
            <FormHelperText id="my-helper-text">Non-sensitive Information Please</FormHelperText>
          </FormControl>
          <FormControl>
            <InputLabel htmlFor="first-name">First Name</InputLabel>
            <Input id="first-name" placeholder="Enter First Name" {...register('firstName', { required: true, max: 16, min: 2 })} />
          </FormControl>
          <FormControl>
            <InputLabel htmlFor="last-name">last Name</InputLabel>
            <Input id="last-name" placeholder="Enter Last Name" {...register('lastName', { required: true, max: 16, min: 2 })} />
          </FormControl>
          <FormControl>
            <InputLabel htmlFor="gender">Gender</InputLabel>
            <Menu id="demo-customized-menu" MenuListProps={{ 'aria-labelledby': 'demo-customized-button' }} anchorEl={null} open={true} onClose={(e) => e}>
              {genderOptions.map((gender: string) => {
                return (
                  <span key={`gender-${gender}`}>
                    <MenuItem onClick={handleGender('black')} value={gender}>
                      {gender}
                    </MenuItem>
                    <Divider />
                  </span>
                );
              })}
            </Menu>
            <Input id="gender" placeholder="Enter Gender" {...register('gender', { required: true })} />
          </FormControl>
          <FormControl>
            <InputLabel htmlFor="last-name">Status</InputLabel>
            <Input id="last-name" placeholder="Enter Status" {...register('status', { required: true })} />
          </FormControl>
          <FormControl>
            <Button type="submit">Create User</Button>
          </FormControl>
        </form>
      </Card>
      {/* Create User
      <div>
        <form onSubmit={handleSubmit(onSubmit)}>
          <input type="email" {...register('email', { required: true, pattern: /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/ })} />
          {errors.email && 'Email is Required'}
          <input {...register('firstName', { required: true })} />
          {errors.firstName && 'First name is Required'}
          <input {...register('lastName', { required: true })} />
          {errors.lastName && 'Last name is Required'}
          <input {...register('gender', { required: true })} />
          {errors.gender && 'Gender is Required'}
          <input {...register('status', { required: true })} />
          {errors.status && 'Status is Required'}
          <button type="submit"> Create </button>
        </form>
      </div> */}
    </div>
  );
};

export default CreateUserCard;
