import { CardActionArea, CardMedia, CardContent, Typography } from '@mui/material';
import Card from '@mui/material/Card';
import React from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';

type Inputs = {
  email: string;
  firstName: string;
  lastName: string;
  gender: string;
  status: string;
};

const CreateUserCard: any = () => {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors }
  } = useForm<Inputs>();

  const onSubmit = (e: any) => {
    console.log(e);
    fetch('https://gorest.co.in/public/v2/users', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: 'Bearer 35fbde49d5eb4a5a443446e7ba864d71ed3969a1ee48247811f6733ebe664c93'
      },
      body: JSON.stringify({
        name: `${e.firstName} ${e.lastName}`,
        email: e.email,
        gender: e.gender,
        status: e.status
      })
    })
      .then((res) => res.json())
      .then((result) => console.log(result))
      .catch((err) => console.log('error'));
  };

  return (
    <div>
      <Card sx={{ maxWidth: 345 }}>
        <CardActionArea>
          <CardMedia component="img" height="140" image="/static/images/cards/contemplative-reptile.jpg" alt="green iguana" />
          <CardContent>
            <Typography gutterBottom variant="h5" component="div">
              Enter Name
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging across all continents except Antarctica
